    videoDownloader: "Trình tải xuống video YouTube",
    mp3Converter: "Trình chuyển đổi YouTube sang MP3",
  },
};

// **自动检测浏览器语言**
const browserLang = navigator.language.split("-")[0]; // 获取 `zh-CN` 的 `zh`
const defaultLang = messages[browserLang] ? browserLang : "en"; // 如果没有匹配语言，使用英语

const i18n = createI18n({
  locale: defaultLang, // 默认语言
  fallbackLocale: "en", // 兜底使用英语
  messages,
});

export default i18n;
